SET search_path TO imgw;

CREATE TABLE station (
    id BIGINT PRIMARY KEY,
    name TEXT NOT NULL,
    latitude NUMERIC,
    longitude NUMERIC,
    a SMALLINT,
    s TEXT,
    id_station_hourly DATE GENERATED ALWAYS AS (12000 + id % 10000) STORED,
);

-- Table with mapping between some id and station_id.
-- Station from hourly reports have different id than from daily report.
CREATE TABLE station_id_mapping (
    id BIGINT PRIMARY KEY,
    station_id BIGINT
);

;